import { Component, OnInit } from '@angular/core';
import { CardService } from '../../services/card.service';
import { Card } from '../../models/card.model';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.css']
})
export class CardComponent implements OnInit{

  color = 'hdcs'
  numberOfCards = 10
  compteur = 0
  show = false
  hand!: Card[]

  constructor(private cardService: CardService){}

  ngOnInit(): void {
    while (this.compteur < this.numberOfCards){
      const valueCard = Math.floor(Math.random()*(14-1) + 1)
      const colorCard = this.color[Math.floor(Math.random()*4)]
      const imgCard = `assets/images/${valueCard}${colorCard}.gif`
      this.cardService.addCard(valueCard, colorCard, imgCard)
      this.compteur += 1;
    }
  }

  showCards(){
    this.hand = this.cardService.hand
    this.show = true
  }
}
<div class="main">
    <div>
        <button (click)="showCarte()">Ajouter des cartes</button>
    </div>
    <div class="hand" *ngIf="show">
        <div *ngFor="let card of hand">
            <div class="card">
                <img [src]="card.img">
            </div>
        </div>
    </div>
    <div>
        <button>Trier carte</button>
    </div>
</div>
