import { Component, OnInit } from '@angular/core';
import { CardService } from '../../services/card.service';
import { Card } from '../../models/card.model';

@Component({
  selector: 'app-card',
  templateUrl: './card.component.html',
  styleUrls: ['./card.component.css']
})
export class CardComponent implements OnInit{

  color = 'hdcs'
  numberOfCards = 10
  compteur = 0
  show = false
  hand!: Card[]

  htab : Card[] = []
  stab : Card[] = []
  dtab : Card[] = []
  ctab : Card[] = []

  constructor(private cardService: CardService){}

  ngOnInit(): void {
    while (this.compteur < this.numberOfCards){
      const valueCard = Math.floor(Math.random()*(14-1) + 1)
      const colorCard = this.color[Math.floor(Math.random()*4)]
      const imgCard = `assets/images/${valueCard}${colorCard}.gif`
      this.cardService.addCard(valueCard, colorCard, imgCard)
      this.compteur += 1;
    }
  }

  showCards(){
    this.hand = this.cardService.hand
    this.show = true
  }

  sortCards(){
    this.hand.map(card=>{
      switch (card.color){
        case 'h': 
          this.htab.push(card)
          break 
        case 's':
          this.stab.push(card)
          break
        case 'd':
          this.stab.push(card)
          break
        case 'c':
          this.ctab.push(card)
      }
    })
    this.htab.sort((a,b)=>a.value-b.value)
    this.stab.sort((a,b)=>a.value-b.value)
    this.dtab.sort((a,b)=>a.value-b.value)
    this.ctab.sort((a,b)=>a.value-b.value)

    this.hand = this.htab.concat(this.stab, this.dtab, this.ctab)
    console.log(this.hand)
  }
}
<div class="main">
    <div>
        <button (click)="showCards()">Ajouter des cartes</button>
    </div>
    <div class="hand" *ngIf="show">
        <div *ngFor="let card of hand">
            <div class="card">
                <img [src]="card.img">
            </div>
        </div>
    </div>
    <div>
        <button (click)="sortCards()">Trier carte</button>
    </div>
</div>
